mapper.update:
UPDATE tbl_repair
SET repair_content = #{content},
repair_status = #{status},
repair_date = #{date}
WHERE res_code = #{resCode}

UPDATE tbl_repair_workers
SET user_code = #{newUserCode}
WHERE res_code = #{resCode}

UPDATE tbl_repair_part
SET part_code = #{newPartCode}
WHERE res_code = #{resCode}
